#!/usr/bin/python3
# -*- coding: utf-8 -*-

WIDTH  = 600
HEIGHT = 400


from tkinter import *
from datetime import *

class Application(Frame):
	"""docstring for Application"""
	def __init__(self, master):
		super(Application, self).__init__(master)
		self.grid()
		#self.bind("<Return>", self.callback) #счет происходит при нажатии на клавишу Enter
		self.AddWidgets()
		

	def AddWidgets(self):

		#label date
		d = date.today()
		d = d.strftime('%A %d %B %Y')
		todayTxt = "Сегодня: " + d
		Label(self,
			  text = todayTxt,
			  font=("Bookman Old Style", 16)
			  ).grid(row = 0, column = 0, columnspan = 2, sticky = W)

		#label  space
		"""Label(self,
			  text = ' ',
			  
			  ).grid(row =1, column = 0, columnspan = 2, sticky = W)"""

		#label дней до з/п
		today = date.today()
		#определяем в каком месяце будет з/п
		if today.day < 20:
		    dateZp = date(today.year, today.month, 20)
		elif today.month+1 == 13:
		    dateZp = date(today.year+1, 1, 20)
		else:
		    dateZp = date(today.year, today.month+1, 20)
		#определяем кол-во дней до з/п
		kolvo = dateZp - today
		if kolvo.days < 0:
		    self.ddd = kolvo.days+30
		else:
		    self.ddd = kolvo.days		    
		#надпись
		dneydozp = 'Дней до з/п: '
		dneydozp = dneydozp + str(self.ddd)
		Label(self, 
			  text = dneydozp, 
			  font=("Bookman Old Style", 14)
			  ).grid(row = 0, column = 2, sticky = W)

		#label enter money and input
		Label(self,
			  text = 'Введите сумму денег: ',
			  font=("Bookman Old Style", 12)
			  ).grid(row = 2, column = 0, columnspan = 2, sticky = W)
		self.money_ent = Entry(self, width = 8, bd = 4 )
		self.money_ent.focus()
		self.money_ent.grid(row = 2, column = 2, sticky = W)

		#label расход в день
		Label(self,
			  text = 'Расход в день: ',
			  font=("Bookman Old Style", 12)
			  ).grid(row = 3, column = 0, columnspan = 2, sticky = W)
		
		self.rashod_lbl_str = StringVar()
		self.rashod_lbl_str.set(" ")
		self.rashod_lbl = Label(self,
		      textvariable=self.rashod_lbl_str,		  
			  font=("Bookman Old Style", 12),
			  bg = "white"
			  )
		self.rashod_lbl.grid(row = 3, column = 1,  sticky = W)
		#self.rashod_lbl["bg"] = "red"
		

		#Button send data
		Button(self,
			text = "Рассчитать",
			command = self.btn_clicked,
			bg = '#293D72', 
			fg = 'white', 
			font=("Bookman Old Style", 30),
			).grid(row = 4, column = 1,  sticky = W)

		#picture label
		#загрузка фото
		photo = """"""
		phot = PhotoImage(data=photo)
		self.phot_lbl = Label(self,
			image = phot
			)
		self.phot_lbl.photo = phot
		self.phot_lbl.grid(row = 4, column = 0,  sticky = W)

		#listbox scrollbar вставка возможность выбора числа з/п
		self.num_scrb = Scrollbar(self)
		self.num_scrb.grid(row = 1, column = 1, sticky = W)

		self.num_lb = Listbox(self, 
			yscrollcommand = self.num_scrb.set, 
			font=("Bookman Old Style", 10),
			height = 2, 
			width = 4)
		for i in range(1,32):
			self.num_lb.insert(i, i)
		self.num_lb.grid(row = 1, column = 0, sticky = E)
		self.num_scrb.config(command = self.num_lb.yview)



	def btn_clicked(self):
		entered = self.money_ent.get()
		#проверка, введено ли целое число или текст
		if entered.isdigit():
			rashVDen = int(entered) / int(self.ddd)
			self.rashod_lbl_str.set(int(rashVDen))
			self.rashod_lbl['bg'] = '#F0F0F0'
		else:
			self.rashod_lbl_str.set('не корректно введена сумма')
			self.rashod_lbl['bg'] = "red"

	


def callback(event):
	Application.btn_clicked(app)


root = Tk()
root.title('Зарплата ver 0.9.2/110517')
root.resizable(0,0)
app = Application(root)
app.bind_all("<Return>", callback)
root.mainloop()

